val countries = sqlc.read.parquet(
    s"$GeonamesDir/countries.parquet"
).filter(
    'geoname_id.isNotNull // geoname_id is null for 'Netherlands Antilles' and 'Serbia and Montenegro' which don't exist anymore
).select(
    'geoname_id, 'iso, 'name, 'continent_name
).withColumnRenamed(
    "geoname_id", "country_geoname_id"
).repartition(NumPartitions).persist(org.apache.spark.storage.StorageLevel.MEMORY_ONLY_SER)

countries.registerTempTable("countries")

val cities = sqlc.read.parquet(
    s"$GeonamesDir/allCountries.parquet"
).filter(
    'feature_class === "P" && 'population >= 500
).withColumn(
    "ascii_alternate_names", onlyAsciiUDF('alternate_names)
).select(
    'geoname_id, 'ascii_name, 'ascii_alternate_names, 'latitude, 'longitude, 'feature_class, 'feature_code, 'admin_1_code, 'country_iso
).withColumnRenamed(
    "geoname_id", "city_geoname_id"
).withColumnRenamed(
    "admin_1_code", "state"
).repartition(NumPartitions).persist(org.apache.spark.storage.StorageLevel.MEMORY_ONLY_SER)

cities.registerTempTable("cities")

def clean(s: String): String = s.toLowerCase().replace("-", " ")

val cleanUDF = udf { (s: String) => clean(s) }

val cleanAlternateNamesUDF = udf { (a: Seq[String], x: String) => if (!a.isEmpty) a.map(clean).filterNot(_ == x) else a }

def properCountryName(s: String): String = countryNameMapping.value.getOrElse(s, s)

val properCountryNameUDF = udf { (s: String) => properCountryName(s) }

val north = "^n (.*)".r
val south = "^s (.*)".r
val east = "^e (.*)".r
val west = "^w (.*)".r
val fort = "^ft (.*)".r
val park = "^(.*) pk$".r
val station = "^(.*) stn$".r
val island = "^(.*) isl$".r
val heights = "^(.*) hts$".r
val junction = "^(.*) jct$".r

def properCityName(s: String): String = cityNameMapping.value.getOrElse(s, """^st(?=e? )""".r.replaceFirstIn(s, "saint") match {
    case north(c) => s"north $c"
    case south(c) => s"south $c"
    case east(c) => s"east $c"
    case west(c) => s"west $c"
    case fort(c) => s"fort $c"
    case park(c) => s"$c park"
    case station(c) => s"$c station"
    case island(c) => s"$c island"
    case heights(c) => s"$c heights"
    case junction(c) => s"$c junction"
    case e => e
})
val properCityNameUDF = udf { (s: String) => properCityName(s) }

val properCityNameArrayUDF = udf { (a: Seq[String]) => a.map(properCityName) }

val cities2 = cities.withColumn(
    "algo_city_name", properCityNameUDF(cleanUDF('ascii_name))
).withColumn(
    "algo_city_alternate_names", properCityNameArrayUDF(cleanAlternateNamesUDF('ascii_alternate_names, 'algo_city_name))
).withColumn(
    "algo_state", cleanUDF('state)
).persist(org.apache.spark.storage.StorageLevel.MEMORY_ONLY_SER)

val countries2 = countries.withColumn(
    "algo_country_name", properCountryNameUDF(cleanUDF('name))
).persist(org.apache.spark.storage.StorageLevel.MEMORY_ONLY_SER)

import scala.util.hashing.MurmurHash3

def hash(a: Seq[String]): Long = MurmurHash3.arrayHash(a.toArray).toLong

val hashUDF = udf { (a: Seq[String]) => hash(a) }

val institutions2 = institutions.withColumn(
    "algo_blocks", algoBlocksUDF('address)
).withColumn(
    "algo_blocks_id", hashUDF('algo_blocks)
).persist(org.apache.spark.storage.StorageLevel.MEMORY_ONLY_SER)

def extractedPercentage(x: Long): Float = (x / extractedCount.toFloat) * 100

def addMetrics(l1: Seq[Int], l2: Seq[Int]): Seq[Int] = (l1, l2).zipped.map(_ + _)

val metrics = extracted.map { case (_, e) =>
    Seq(
        if (e.isIdentified) 1 else 0,
        if (e.isAmbiguous) 1 else 0,
        if (e.countryId.isEmpty) 1 else 0,
        if (e.cityIds.isEmpty) 1 else 0)
}.aggregate(Seq.fill(4)(0))(addMetrics, addMetrics).map(_.toLong)

println("Metrics on distinct (block2, block1, block0) in lower case:")

Seq(
    (extractedCount, "total"),
    (metrics(0), "identified"),
    (metrics(1), "ambiguous"),
    (metrics(2), "no country"),
    (metrics(3), "no city")
).map {
    case (m, label) => f"$m%.0f (${extractedPercentage(m)}%.2f %%) $label"
}.foreach(println)

val identified = extracted.filter(_._2.isIdentified).map { case (blocksId, e) => (blocksId, e.cityIds.map(_.head)) }.toDF("algo_geo_id", "city_geoname_id")

val institutions3 = institutions2.join(
    identified, 'algo_blocks_id === 'algo_geo_id, "left_outer"
).select(
    // original fields
    'pub_id, 'idx, 'address, 'enhanced_names,
    // GeoNames identification result fields
    'city_geoname_id
    // GeoNames identification debug fields
    //'algo_geo_id
).coalesce(NumPartitions).persist(org.apache.spark.storage.StorageLevel.MEMORY_ONLY_SER)
