// "is_valid = true" by design
val institutions = sqlc.read.parquet(s"$DisambigDir/geonames_city_identified_institutions-did_$DatasetId.parquet").persist(org.apache.spark.storage.StorageLevel.MEMORY_ONLY_SER)

institutions.registerTempTable("institutions")

//assert(institutions.select('address).distinct.count() != institutions.selectExpr("lower(address)").distinct.count())
// addresses ARE case sensitive

val enhancedNames = institutions.explode("enhanced_names", "enhanced_name")((a: Seq[String]) => Option(a).getOrElse(Seq.empty)).select('enhanced_name).distinct.persist(org.apache.spark.storage.StorageLevel.MEMORY_ONLY_SER)
assert(enhancedNames.count() == enhancedNames.selectExpr("lower(enhanced_name)").distinct.count())
// enhanced names are NOT case sensitive

import scala.util.hashing.MurmurHash3

def hash(s: String): Long = MurmurHash3.stringHash(s).toLong

val hashUDF = udf { (s: String) => hash(s) }

val institutions2 = institutions.withColumn(
    "lc_address", lower('address)
).withColumn(
    "lc_address_id", hashUDF('lc_address)
).persist(org.apache.spark.storage.StorageLevel.MEMORY_ONLY_SER)

institutions2.registerTempTable("institutions_2")

val aggregatedEnhancedNames = institutions2.select('lc_address_id, 'enhanced_names).map { r =>
    (r.getLong(0), collection.mutable.Set(Option(r.getSeq[String](1)).getOrElse(Seq.empty):_*))
}.reduceByKey(_ ++= _).mapValues(e => if (e.isEmpty) None else Some(e.toSeq.sorted)).toDF("address_dis_id", "aggregated_enhanced_names").persist(org.apache.spark.storage.StorageLevel.MEMORY_ONLY_SER)

val institutions3 = institutions2.withColumn(
    "first_block", firstBlockUDF('address)
).join(
    aggregatedEnhancedNames, 'lc_address_id === 'address_dis_id
).persist(org.apache.spark.storage.StorageLevel.MEMORY_ONLY_SER)

institutions3.registerTempTable("institutions_3")

val institutions4 = institutions3.withColumn(
    "institution_names", chooseUDF('aggregated_enhanced_names, 'first_block)
).select(
    // original fields
    'pub_id, 'idx, 'address, 'enhanced_names,
    // GeoNames identification result fields (from previous step)
    'city_geoname_id,
    // institution names identification result fields
    'address_dis_id, 'institution_names
    // institution names identification debug fields
    //'aggregated_enhanced_names
).coalesce(NumPartitions).persist(org.apache.spark.storage.StorageLevel.MEMORY_ONLY_SER)
