val institutionsSelfIndicators = institutionsToPublications.filter(
    'pub_dis_id.isNotNull && 'is_main === true
).groupBy(
    'inst_name
).agg(
    sum('pub_impact) as "inst_impact",
    count('pub_dis_id) as "inst_pub_count",
    sum('is_conference.cast("int")) as "inst_conf_count",
    min('year) as "inst_first_pub_year"
)

val institutionDepths = institutionsToPublications.groupBy(
    'inst_name
).agg(
    max('is_main) as "is_from_main_pub"
)

val institutionIndicatorsFromAuthors = institutionsToAuthors.filter(
    'pub_dis_id.isNotNull && 'author_dis_id.isNotNull && 'year === 'author_latest_pub_year && 'is_main === true
).groupBy(
    'inst_name
).agg(
    "pub_impact" -> "sum",
    "author_dis_id" -> "count"
).withColumnRenamed(
    "SUM(pub_impact)", "inst_impact_from_authors"
).withColumnRenamed(
    "COUNT(author_dis_id)", "inst_author_count"
)

val institutionIndicators = institutionsSelfIndicators.as("i1").join(
    institutionDepths.as("i2"), $"i1.inst_name" === $"i2.inst_name", "left_outer"
).join(
    institutionIndicatorsFromAuthors.as("i3"), $"i1.inst_name" === $"i3.inst_name", "left_outer"
).select(
    $"i1.inst_name", 'is_from_main_pub, 'inst_impact, 'inst_impact_from_authors, 'inst_author_count, 'inst_pub_count, 'inst_conf_count, 'inst_first_pub_year
).coalesce(NumPartitions).persist(org.apache.spark.storage.StorageLevel.MEMORY_ONLY_SER)

val countries = sqlc.read.parquet(
    s"$GeonamesDir/countries.parquet"
).filter(
    'geoname_id.isNotNull // geoname_id is null for 'Netherlands Antilles' and 'Serbia and Montenegro' which don't exist anymore
).select(
    'geoname_id, 'iso, 'name, 'continent_name
).withColumnRenamed(
    "geoname_id", "country_geoname_id"
).repartition(NumPartitions).persist(org.apache.spark.storage.StorageLevel.MEMORY_ONLY_SER)

val cityIndicators = institutionIndicators.filter(
    'is_from_main_pub === true
).withColumnRenamed(
    "inst_name", "org_inst_name"
).join(
    institutionsCities, 'org_inst_name === 'inst_name
).filter(
    'city_geoname_id.isNotNull // "year = author_latest_pub_year" by design (cf institutionIndicators)
).groupBy(
    'city_geoname_id
).agg(
    sum('inst_impact) as "city_impact",
    sum('inst_impact_from_authors) as "city_impact_from_authors",
    sum('inst_author_count) as "city_author_count",
    sum('inst_pub_count) as "city_pub_count",
    sum('inst_conf_count) as "city_conf_count"
).persist(org.apache.spark.storage.StorageLevel.MEMORY_ONLY_SER)
