// FIXME Disambiguate publications

val authorsToPublications = authors.join(
    publicationIndicators, 'author_pub_id === 'pub_id, "left_outer"
).coalesce(NumPartitions).persist(org.apache.spark.storage.StorageLevel.MEMORY_ONLY_SER)

authorsToPublications.registerTempTable("authors_to_publications")

val commonAuthorIndicators = authorsToPublications.filter(
    'pub_dis_id.isNotNull
)

val mainAuthorIndicators = authorsToPublications.filter(
    'pub_dis_id.isNotNull && 'is_main === true
).groupBy(
    'author_dis_id, 'author_dis_name
).agg(
    sum('pub_impact) as "author_impact",
    count('pub_id) as "author_pub_count",
    sum('is_conference.cast("int")) as "author_conf_count",
    min('year) as "author_first_pub_year",
    max('year) as "author_latest_pub_year"
)

val authorIndicators = commonAuthorIndicators.as("a1").join(
    mainAuthorIndicators.as("a2"), $"a1.author_dis_id" === $"a2.author_dis_id", "left_outer"
).select(
    $"a1.author_dis_id" as "author_dis_id", $"a1.author_dis_name" as "author_dis_name", 'author_first_pub_year, 'author_latest_pub_year, 'author_impact, 'author_pub_count, 'author_conf_count
).distinct.coalesce(NumPartitions).persist(org.apache.spark.storage.StorageLevel.MEMORY_ONLY_SER)

authorIndicators.registerTempTable("author_indicators")
