%sql
select *
from (
    select lc_title, title_set, size(title_set) as s
    from (
        select lc_title, collect_set(title) as title_set
        from (select lower(title) as lc_title, title from publications) t1
        group by lc_title
        ) t2
    ) t3
where s > 1
order by s desc
limit 8

import scala.util.hashing.MurmurHash3

def hash(s: String): Long = MurmurHash3.stringHash(s).toLong

val hashUDF = udf { (s: String) => hash(s) }

val publications4 = publications.withColumn(
    "dis_title", upper('title)
).withColumn(
    "dis_id", 'pub_id
).select(
    // original fields
    'pub_id, 'is_main, 'is_backward, 'is_forward, 'title, 'year, 'times_cited, 'document_types, 'abstract, 'is_conference, 'author_keywords, 'keywords_plus, 'research_areas, 'wos_categories, 'scielo_categories, 'scielo_collections,
    // disambiguation result fields
    'dis_id, 'dis_title
).persist(org.apache.spark.storage.StorageLevel.MEMORY_ONLY_SER)

publications4.registerTempTable("publications_4")

%sql
select c, hashUDF(lc_abstract) as lc_abstract_hash, dis_id, pub_id, dis_title, year, times_cited, document_types
from (
    select lc_abstract, count(*) c
    from (
        select lower(abstract) as lc_abstract
        from (
            select distinct abstract
            from publications_4
            ) t1
        ) t2
    group by lc_abstract
    ) t3
join publications_4
on lower(abstract) = lc_abstract
where c > 1
order by c, lc_abstract_hash desc
